Vue.component("cadastro",{template:'<form class="form-signin" v-on:submit="cadastro($event)">\n        \x3c!--titulo formulario--\x3e\n        <h1 class="h3 mb-3 font-weight-normal">Registrar</h1>\n        <hr>\n        \x3c!--Nome--\x3e\n        <input type="text" class="form-control" placeholder="Nome"  v-model="nome" required autofocus><br>\n        \x3c!--email--\x3e\n        <input type="email"  class="form-control" placeholder="Email" v-model="email" required=""><br>\n        \x3c!--senha--\x3e\n        <input type="password"  class="form-control" placeholder="Senha" v-model="senha" required="">\n        \x3c!--botao de logar--\x3e\n        <button class="btn btn-lg btn-success btn-block" type="submit" >Registrar</button>\n    </form>',data:function(){return{nome:"",email:"",senha:""}},methods:{cadastro(n){n.preventDefault();let a=new FormData;a.append("nome",this.nome),a.append("email",this.email),a.append("senha",this.senha);fetch("/cadastrar",{method:"POST",body:a}).then(function(n){return n.json()}).then(function(n){n.error?alert("Não foi possivel cadastrar"):alert("Cadastrado com sucesso")})}}}),Vue.component("login",{template:'<form class="form-signin"  v-on:submit="login($event)">\n        \x3c!--titulo formulario--\x3e\n        <h1 class="h3 mb-3 font-weight-normal">Entrar</h1>\n        <hr>\n        \x3c!--email--\x3e\n        <input type="email" class="form-control" placeholder="Email"  v-model="email" required autofocus><br>\n        \x3c!--senha--\x3e\n        <input type="password" class="form-control" placeholder="Senha" v-model="senha" required="">\n        \x3c!--botao de logar--\x3e\n        <button class="btn btn-lg btn-primary btn-block" type="submit">Logar</button>\n    </form>',data:function(){return{email:"",senha:""}},methods:{login(n){n.preventDefault();let a=new FormData;a.append("email",this.email),a.append("senha",this.senha);fetch("/login",{method:"POST",body:a}).then(function(n){return n.json()}).then(function(n){n.error?alert("Dados incorretos"):window.location.href="/"})}}}),Vue.component("menu-quiz",{props:["usuario"],template:'<nav class="navbar navbar-expand-lg navbar-light navbar-laravel">\n            <div class="container">\n                <a class="navbar-brand" href="/">QUIZ</a>\n                <ul class="navbar-nav ml-auto">\n                    <li class="nav-item" v-if="usuario == null">\n                        <a class="nav-link" href="/login">Login</a>\n                    </li>\n                    <li class="nav-item" v-if="usuario == null">\n                         <a class="nav-link" href="/registro">Registrar</a>\n                    </li>\n                     <li class="nav-item" v-if="usuario != null">\n                         <span class="font-weight-bold">Olá, {{usuario.nome}}</span>\n                    </li>\n                    <li class="nav-item" v-if="usuario != null">\n                         <button class="btn  btn-secondary btn-block" v-on:click="logout" >Sair</button>\n                    </li>\n                </ul>\n            </div>\n        </nav>',data:function(){return{sair:""}},methods:{logout(){fetch("/logout",{method:"GET"}).then(function(n){return n.json()}).then(function(){window.location.href="/login"})}}});